// src/components/Dashboard.js
import React, { useState, useEffect } from "react";
import axios from "axios";
import "./Dashboard.css";

const Dashboard = () => {
  const [selectedFile, setSelectedFile] = useState(null);
  const [historyImages, setHistoryImages] = useState([]);
  const [message, setMessage] = useState("");
  const [latestResult, setLatestResult] = useState(null);
  const token = localStorage.getItem("access_token");

  useEffect(() => {
    fetchHistory();
  }, []);

  // Fetch the history of uploads for the current user
  const fetchHistory = async () => {
    try {
      const res = await axios.get("http://localhost:5000/api/history", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setHistoryImages(res.data);
    } catch (error) {
      console.error("Failed to fetch history", error);
    }
  };

  const handleFileChange = (e) => {
    setSelectedFile(e.target.files[0]);
  };

  // Upload the image and update the UI with the new detection result
  const handleUpload = async (e) => {
    e.preventDefault();
    if (!selectedFile) return;
    const formData = new FormData();
    formData.append("image", selectedFile);

    try {
      const res = await axios.post(
        "http://localhost:5000/api/upload",
        formData,
        {
          headers: {
            "Content-Type": "multipart/form-data",
            Authorization: `Bearer ${token}`,
          },
        }
      );
      // Set a success message and show the latest result image right away
      setMessage(res.data.msg);
      setLatestResult(
        `http://localhost:5000/uploads/results/${res.data.result_filename}`
      );
      setSelectedFile(null);
      fetchHistory();
    } catch (error) {
      console.error("Upload failed", error);
      setMessage("Upload failed");
    }
  };

  return (
    <div className="dashboard-container fade-in">
      <h2>Dashboard</h2>
      <form onSubmit={handleUpload} className="upload-form">
        <input
          type="file"
          onChange={handleFileChange}
          accept="image/*"
          required
        />
        <button type="submit" className="btn">
          Upload & Detect
        </button>
      </form>
      {message && <p className="message">{message}</p>}

      {/* Latest Detection Result Section */}
      {latestResult && (
        <div className="latest-result">
          <h3>Latest Detection Result</h3>
          <img src={latestResult} alt="Latest detection result" />
        </div>
      )}

      <hr />
      <h3>Your Upload History</h3>
      <div className="history-grid">
        {historyImages.map((item) => (
          <div key={item.id} className="history-card">
            <img
              src={`http://localhost:5000/uploads/results/${item.result_filename}`}
              alt={item.original_filename}
            />
            <p>{item.timestamp}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Dashboard;
