// src/components/Dashboard.js

import React, { useState, useEffect } from "react";
import axios from "axios";
import "./Dashboard.css";

const Dashboard = () => {
  const [selectedFile, setSelectedFile] = useState(null);
  const [isVideo, setIsVideo] = useState(false);
  const [historyItems, setHistoryItems] = useState([]);
  const [message, setMessage] = useState("");
  const [latestResult, setLatestResult] = useState(null);
  const [latestIsVideo, setLatestIsVideo] = useState(false);

  const token = localStorage.getItem("access_token");
  const API_BASE = "http://localhost:5000/api";

  useEffect(() => {
    fetchHistory();
  }, []);

  const fetchHistory = async () => {
    try {
      const res = await axios.get(`${API_BASE}/history`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      // Expect each item to have: id, original_filename, result_filename, timestamp, type
      setHistoryItems(res.data);
    } catch (err) {
      console.error("Failed to fetch history", err);
    }
  };

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    setSelectedFile(file);
    if (file && file.type.startsWith("video/")) {
      setIsVideo(true);
    } else {
      setIsVideo(false);
    }
  };

  const handleUpload = async (e) => {
    e.preventDefault();
    if (!selectedFile) return;

    const formData = new FormData();
    // key must match what Flask expects
    formData.append(isVideo ? "video" : "image", selectedFile);

    const url = isVideo ? `${API_BASE}/upload_video` : `${API_BASE}/upload`;

    try {
      const res = await axios.post(url, formData, {
        headers: {
          "Content-Type": "multipart/form-data",
          Authorization: `Bearer ${token}`,
        },
      });

      setMessage(res.data.msg);

      const resultPath = `http://localhost:5000/uploads/results/${res.data.result_filename}`;
      setLatestResult(resultPath);
      setLatestIsVideo(isVideo);

      // reset
      setSelectedFile(null);
      setIsVideo(false);

      // reload history
      fetchHistory();
    } catch (err) {
      console.error("Upload failed", err);
      setMessage("Upload failed");
    }
  };

  return (
    <div className="dashboard-container fade-in">
      <h2>Dashboard</h2>

      <form onSubmit={handleUpload} className="upload-form">
        <input
          type="file"
          onChange={handleFileChange}
          accept="image/*,video/*"
          required
        />
        <button type="submit" className="btn">
          Upload & Detect
        </button>
      </form>

      {message && <p className="message">{message}</p>}

      {latestResult && (
        <div className="latest-result">
          <h3>Latest Detection Result</h3>
          {latestIsVideo ? (
            <video src={latestResult} controls style={{ maxWidth: "100%" }} />
          ) : (
            <img
              src={latestResult}
              alt="Latest detection result"
              style={{ maxWidth: "100%" }}
            />
          )}
        </div>
      )}

      <hr />

      <h3>Your Upload History</h3>
      <div className="history-grid">
        {historyItems.map((item) => (
          <div key={item.id} className="history-card">
            {item.type === "video" ? (
              <video
                src={`http://localhost:5000/uploads/results/${item.result_filename}`}
                controls
                style={{ maxWidth: "100%" }}
              />
            ) : (
              <img
                src={`http://localhost:5000/uploads/results/${item.result_filename}`}
                alt={item.original_filename}
                style={{ maxWidth: "100%" }}
              />
            )}
            <p>{item.timestamp}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Dashboard;
